mapper.select:
SELECT

FROM bill b
LEFT JOIN category c ON b.category_id = c.id
WHERE b.deleted = 0
  AND c.deleted = 0
  AND b.user_id = #{userId}

    AND b.bill_date >= #{query.startDate}


    AND b.bill_date <= #{query.endDate}


    AND b.type = #{query.type}


    AND b.category_id = #{query.categoryId}

ORDER BY b.bill_date DESC, b.create_time DESC
